import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; // Import Link from react-router-dom
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faCheck, faTimes } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

export default function AddClient() {
  const navigate = useNavigate();

  const [clientDto, setClientDto] = useState({
    firstname: "",
    surname: "",
    email:"",
    phone_number:"",
    age: 0,
    password:"",
  });

  const onInputChange = (event) => {
    const { name, value } = event.target;
    setClientDto({ ...clientDto, [name]: value });
  };

  const { firstname, surname, email, phone_number, age, password} = clientDto;

  const onSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post('http://localhost:8080/api/clients/saveClient', clientDto);
      navigate('/');
    } catch (error) {
      console.error('Error occurred while adding category:', error);
      // Handle error scenarios here
    }
  };

  return (
    <div>
      <h2>Add Client</h2>
      <form onSubmit={(event) => onSubmit(event)}>
        <div className="mb-3 d-flex align-items-center">
          <label htmlFor="Name" className="form-label with-shadow">
            <FontAwesomeIcon icon={faUser} className="mr-2" style={{ color: '#9400D3' }} />
          </label>
          <input
            type="text"
            className="form-control with-shadow custom-input"
            placeholder="Name"
            name="name"
            value={firstname}
            onChange={(event) => onInputChange(event)}
          />
        </div>
        <div className="mb-3 d-flex align-items-center">
          <label htmlFor="Description" className="form-label with-shadow">
            <FontAwesomeIcon icon={faUser} className="mr-2" style={{ color: '#9400D3' }} />
          </label>
          <input
            type="text"
            className="form-control with-shadow custom-input"
            placeholder="Surname"
            name="surname"
            value={surname}
            onChange={(event) => onInputChange(event)}
          />
          <input
            type="email"
            className="form-control with-shadow custom-input"
            placeholder="Email"
            name="Email"
            value={email}
            onChange={(event) => onInputChange(event)}
          />
          <input
            type="text"
            className="form-control with-shadow custom-input"
            placeholder="number"
            name="Phone Number"
            value={phone_number}
            onChange={(event) => onInputChange(event)}
          />
          <input
            type="age"
            className="form-control with-shadow custom-input"
            placeholder="age"
            name="Age"
            value={age}
            onChange={(event) => onInputChange(event)}
          />
          <input
            type="password"
            className="form-control with-shadow custom-input"
            placeholder="password"
            name="Password"
            value={password}
            onChange={(event) => onInputChange(event)}
          />
        </div>
        <button
          type="submit" 
          className="btn btn-primary mx-2"
          style={{ backgroundColor: '#9400D3', borderColor: '#9400D3' }}
        >
          <FontAwesomeIcon icon={faCheck} />
        </button>
        <Link className="btn btn-primary mx-2" style={{ backgroundColor: '#9400D3', borderColor: '#9400D3' }} to="/">
          <FontAwesomeIcon icon={faTimes} />
        </Link>
      </form>
    </div>
  );
}
